import {useState} from "react";

const intialUserInput = {
    'current-savings' :1000,
    'yearly-contribution' : 1200,
    'expected-return' : 7 ,
    'duration': 10
}
const UserInput = () =>{

const [userInput,setUserInput]= useState(intialUserInput);
    const onSubmitHandler = (event)=>
    {
        event.preventDefault();
        console.log('Submit');
    }

    const resetHandler = () =>{
        setUserInput(intialUserInput);
    }

    const inputHandler =(input,value) =>{
        setUserInput((prevInput)=> {
            return{...prevInput,
            [input]: value,
            };
        });

    };
    return (
        <form className="form" onSubmit={onSubmitHandler}>
            <div className="input-group">
                <p>
                    <label htmlFor="current-savings">Current Savings ($)</label>
                    <input type="number" id="current-savings"
                           onChange={(event)=>{
                               inputHandler('current-savings' ,event.target.value)}} />
                </p>
                <p>
                    <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                    <input type="number" id="yearly-contribution"
                           onChange={(event)=>{
                        inputHandler('yearly-contribution' ,event.target.value)}} />
                </p>
            </div>
            <div className="input-group">
                <p>
                    <label htmlFor="expected-return">
                        Expected Interest (%, per year)
                    </label>
                    <input type="number" id="expected-return"
                           onChange={(event)=>{
                               inputHandler('expected-return' ,event.target.value)}}/>
                </p>
                <p>
                    <label htmlFor="duration">Investment Duration (years)</label>
                    <input type="number" id="duration" value ={userInput["duration"]}
                           onChange={(event)=>{
                               inputHandler('duration' ,event.target.value)}}/>
                </p>
            </div>
            <p className="actions">
                <button type="reset" className="buttonAlt" onClick={resetHandler}>
                    Reset
                </button>
                <button type="submit" className="button">
                    Calculate
                </button>
            </p>
        </form>

    )
}
export default UserInput;
